-- Create "namespaces" table
CREATE TABLE "namespaces" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "deployment_package_namespaces" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "namespaces_deployment_packages_namespaces" FOREIGN KEY ("deployment_package_namespaces") REFERENCES "deployment_packages" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create "namespace_adornments" table
CREATE TABLE "namespace_adornments" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "type" character varying NOT NULL, "key" character varying NOT NULL, "value" character varying NULL, "namespace_adornments" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "namespace_adornments_namespaces_adornments" FOREIGN KEY ("namespace_adornments") REFERENCES "namespaces" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
